import React, { Component } from 'react';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import Navigation from './components/Navigation/Navigation';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';

import './App.css';

const app = new Clarifai.App({
  apiKey: '9fed7a4c25f2495fba04392f107796c0'
});

const particulesOptions = {
  particles: {
    number: {
      value: 100,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}
class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: ''
    }
  }
  onInputChange = (event) => {
    this.setState({ input: event.target.value });
  }
  onButtonSubmit() {
    this.setState({ imageUrl: this.state.input });
    app.models.predict(Clarifai.GENERAL_MODEL, "https://samples.clarifai.com/face-det.jpg").then(
      function (response) {
        console.log(response);
      },
      function (err) {
        // there was an error
      }
    );
  }
  render() {
    return (
      <div className="App">
        <Particles className="particles" params={particulesOptions} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit} />
        <FaceRecognition imageUrl='{this.state.imageUrl}' />
      </div>
    );
  }
}

export default App;
